import React, { Component } from 'react'
import { Switch, Route, Redirect } from 'react-router-dom'
import { UserSession } from 'blockstack'
import EditMe from './EditMe'
import Kingdom from './Kingdom'
import NavBar from './NavBar'
import OptionsList from './OptionsList'
import { appConfig, ME_FILENAME } from './constants'
import './SignedIn.css'

let readings = []
class SignedIn extends Component {

  constructor(props) {
    super(props)
    this.userSession = new UserSession({ appConfig })
    this.state = {
      me: {},
      savingMe: false,
      savingKingdown: false,
      redirectToMe: false
      //selectedAnimal: false,
      //selectedTerritory: false
    }
    this.loadMe = this.loadMe.bind(this)
    this.saveMe = this.saveMe.bind(this)
    this.signOut = this.signOut.bind(this)
  }

  componentWillMount() {
    this.loadMe()
  }

  loadMe() {
    let options = { encrypt: true }
    this.userSession.putFile(ME_FILENAME, JSON.stringify({"A":"a"}), options)
    options = { decrypt: true }
    this.userSession.getFile(ME_FILENAME, options).then((content) =>{
      if(content){
        console.log("YES")
      } else {
        console.log("NO")
      }
      /*
      for(var i in JSON.parse(content))
        readings.push(results[i])
        console.log(results[i])
        */
    })
    /*
    .then((content) => {
      if(content) {
        const me = JSON.parse(content)
        readings.push("Else")
        this.setState({me, redirectToMe: false})
        for(var i in me)
          readings.push(me[i])
        readings.push("ELSE")
      } else {
        const me = null
        readings.push("Else")
        this.setState({me, redirectToMe: true})
      }
    })*/
  }

  saveMe(me) {
    this.setState({me, savingMe: true})
    const options = { encrypt: true }
    this.userSession.putFile(ME_FILENAME, JSON.stringify(me), options)
    .finally(() => {
      this.setState({savingMe: false, redirectToMe: false})
    })
  }

  signOut(e) {
    e.preventDefault()
    this.userSession.signUserOut()
    window.location = '/'
  }

  render() {
    const username = this.userSession.loadUserData().username
    const me = this.state.me
    const redirectToMe = this.state.redirectToMe
    if(redirectToMe) {
      // User hasn't configured their animal
      if(window.location.pathname !== '/me') {
        return (
          <Redirect to="/me" />
        )
      }
    }

    if(window.location.pathname === '/') {
      return (
        <Redirect to={`/kingdom/${username}`} />
      )
    }


    return (
      <div className="SignedIn">
      <NavBar username={username} signOut={this.signOut}/>
      <Switch>
              <Route
                path={`/kingdom/${username}`}
                render={
                  routeProps => <Kingdom
                  myKingdom={true}
                  protocol={window.location.protocol}
                  ruler={username}
                  currentUsername={username}
                  realm={window.location.origin.split('//')[1]}
                  {...routeProps} />
                }
              />
              /*
              <Route
                path='/kingdom/:protocol/:realm/:ruler'
                render={
                  routeProps => <Kingdom
                  myKingdom={false}
                  protocol={routeProps.match.params.protocol}
                  realm={routeProps.match.params.realm}
                  ruler={routeProps.match.params.ruler}
                  currentUsername={username}
                  {...routeProps} />
                }
              />*/
      </Switch>
      </div>
    );
  }
}
export {readings}
export default SignedIn
