import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { UserSession } from 'blockstack'
import { jsonCopy} from './utils'
import { appConfig, EXPLORER_URL } from './constants'

import './Kingdom.css'

class Kingdom extends Component {

  constructor(props) {
    super(props)
    this.state = {
      value: '',
      app:`${props.protocol}//${props.realm}`,
      rulerUsername: "Anthony",
      clickAdd: false,
      readings:"3"
    }
    this.readings = "4"
    this.userSession = new UserSession({ appConfig })
    this.handleChange = this.handleChange.bind(this)
    window.addEventListener("submit", () => {
      this.readings += "3";
    });
  }

  componentWillMount() {
    const app = this.state.app
    const ruler = this.props.ruler
    const search = window.location.search
    if(search) {
      const appUrl = search.split('=')[1]
      this.setState({
        value: appUrl,
        clickAdd: true
      })
    }
  }

  componentWillReceiveProps(nextProps) {
    const nextSubjects = nextProps.subjects
    if(nextSubjects) {
      if (nextSubjects.length !== this.state.subjects.length) {
        this.setState({ subjects: jsonCopy(nextSubjects) })
      }
    }
  }

  handleChange(event) {
   this.setState({value: event.target.value});
  }

  render() {
    const mine = this.props.myKingdom
    const username = this.state.rulerUsername
    const app = this.state.app
    const clickAdd = this.state.clickAdd
    const currentUsername = this.props.currentUsername
    const readings = this.readings
    return (
      <div className="Kingdom">
        <div className="row">
        </div>
        <div className="row ruler">
          <div className="col-lg-12">
            <h2>Welcome {username}</h2>
            <h2> {readings} </h2>
            <div className="container">
              <h2>Add Reading</h2>
              {mine ?
                <div className="row justify-content-center">
                  <div
                    id="addReading"
                    className="add-frame col-lg-8"
                    style={{borderColor: (clickAdd ? 'red' : '#f8f9fa')}}
                  >
                    <form onSubmit={this.addReading} className="input-group">
                      <input
                        className="form-control"
                        type="text"
                        onChange={this.handleChange}
                        value={this.state.value}
                        required
                        placeholder="Glucose value"
                      />
                      <div className="input-group-append">
                        <input type="submit" className="btn btn-primary" value="Add reading"/>
                      </div>
                    </form>
                  </div>
                </div>
                :
                null
              }
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Kingdom
