import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { UserSession } from 'blockstack'
import { jsonCopy} from './utils'
import { appConfig, ME_FILENAME, EXPLORER_URL } from './constants'
import {READINGS} from './SignedIn'
import CanvasJSReact from './canvasjs.react'


import './Kingdom.css'

var CanvasJS = CanvasJSReact.CanvasJS
var CanvasJSChart = CanvasJSReact.CanvasJSChart

let data = [
    { y: 90  },
    { y: 80  },
    { y: 92  },
    { y: 110  },
    { y: 105 },
]

class Kingdom extends Component {

  constructor(props) {
    super(props)
    this.state = {
      value: '',
      app:`${props.protocol}//${props.realm}`,
      rulerUsername: "Anthony",
      clickAdd: false,
      readings:[],
      newreadings:[]
    }
    this.userSession = new UserSession({ appConfig })
    this.handleChange = this.handleChange.bind(this)
  }

  componentWillMount() {
    const app = this.state.app
    const ruler = this.props.ruler
    const search = window.location.search
    if(search) {
      const appUrl = search.split('=')[1]
      this.setState({
        value: appUrl,
        clickAdd: true
      })
    }
    this.loadMe()

  }

  componentWillReceiveProps(nextProps) {
    const nextSubjects = nextProps.subjects
    if(nextSubjects) {
      if (nextSubjects.length !== this.state.subjects.length) {
        this.setState({ subjects: jsonCopy(nextSubjects) })
      }
    }
  }

  handleChange(event) {
   this.setState({value: event.target.value});
  }

  loadMe() {
/*
    let options = { encrypt: true }
    this.userSession.putFile(ME_FILENAME, JSON.stringify([{y:90},{y:101},{y:108},{y:98}]), options)
*/
    let temp = []
    let options = { decrypt: true }
    this.userSession.getFile(ME_FILENAME, options).then(async (content) =>{
      if(content){
        temp = await JSON.parse(content)
        this.setState({readings:temp})
      }
    })
  }

  addReading(){/*
    let options = { encrypt: true }
    this.state.readings.push({y:this.state.value})
    this.userSession.putFile(ME_FILENAME, JSON.stringify(this.state.readings), options)
    */this.setState({newreadings:[7]})
  }

  render() {
    const mine = this.props.myKingdom
    const username = this.state.rulerUsername
    const app = this.state.app
    const clickAdd = this.state.clickAdd
    const currentUsername = this.props.currentUsername
    const options = {
      axisY:{
        minimum:60
      },
      data: [{
                type: "line",
                dataPoints:this.state.readings
       }]
   }

    return (
      <div className="Kingdom">
        <div className="row">
        </div>
        <div className="row ruler">
          <div className="col-lg-12">
            <h2>{username}'s Glucose Readings</h2>
            <div>
              <CanvasJSChart options = {options}/>
            </div>
            <div className="container">
              <h2>Add Reading</h2>
              {mine ?
                <div className="row justify-content-center">
                  <div
                    id="addReading"
                    className="add-frame col-lg-8"
                    style={{borderColor: (clickAdd ? 'red' : '#f8f9fa')}}
                  >
                    <form onSubmit={this.addReading} className="input-group">
                      <input
                        className="form-control"
                        type="number"
                        onChange={this.handleChange}
                        value={this.state.value}
                        required
                        placeholder="Glucose value"
                      />
                      <div className="input-group-append">
                        <input type="submit" className="btn btn-primary" value="Add reading"/>
                      </div>
                    </form>
                  </div>
                </div>
                :
                null
              }
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Kingdom
